mapper.select:
SELECT board_idx, title, contents, hit_cnt, DATE_FORMAT(create_datetime, '%Y-%m-%d') AS create_datetime, creator_id
FROM T_BOARD
WHERE delete_yn = 'N' AND board_idx = #{boardIdx}

mapper.update:
UPDATE T_BOARD
SET hit_cnt = hit_cnt + 1
WHERE board_idx = #{boardIdx};

UPDATE T_BOARD
SET delete_yn = 'Y'
WHERE board_idx = #{boardIdx};

mapper.insert:
INSERT INTO T_FILE( board_idx, original_file_name, stored_file_path, file_size, creator_id, create_datetime ) SELECT A.* FROM(
SELECT #{item.boardIdx} as board_idx, #{item.originalFileName} as original_file_name, #{item.storedFilePath} as stored_file_path, #{item.fileSize} as file_size, 'admin' as creator_id, SYSDATE() as create_datetime FROM DUAL
) A

mapper.select:
SELECT idx, board_idx, original_file_name, ROUND(file_size/1024) as file_size
FROM t_file
WHERE board_idx = #{boardIdx};
